catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var gl = new GlideAjax('global.getLocation'); //getLocation is the script include class
   gl.addParam('sysparm_name', 'locationInfo'); //locationDetail is the script include method
   gl.addParam('sysparm_user_sysid', g_form.getValue('requested_for')); //set parameter sysparm_user_sysid to 'requested_for'
   gl.getXML(setLocation); 
   /*call getLocation.locationInfo() with the parameter sysparm_user_sysid set to 'requested_for' 
   and use the callback function setLocation() to return the result when ready */


}
// the callback function for returning the result of the server side code
function setLocation(response) {
	var answer = response.responseXML.documentElement.getAttribute('answer');
	g_form.setValue('office_location', answer);
}
