sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

    var lvsgr = new GlideRecord('x_823134_lva_vehicle_servicing'); //Create a variable for a new GlideRecord on the servicing table
    lvsgr.addQuery('u_reference_1', current.sys_id); //Query the servicing table for the current sys_id of the record
    //that is in the vehicle tracker field
    lvsgr.addQuery('u_choice_1', 'open'); //Query the servicing table for records where the tracker status is open
    lvsgr.query(); //Execute the query

    if (!lvsgr.next()) { //Create a “if statement”: if no matching records are found from the previous query move on with the script: .next()

        var lvstgr = new GlideRecord('x_823134_lva_vehicle_servicing'); //Create a variable for a new GlideRecord on the servicing table
        var currDate = new GlideDate(); //Create a variable for a new GlideDate that will get the current date 
        //( this will be used to set the current date in the servicing ticket’s opening date)

        lvstgr.initialize(); //Initialize the creation of a new record on the servicing table using the GlideRecord variable you created
        //Populate the required fields to create the new ticket per the stories requirement
        lvstgr.setValue('u_reference_1', current.sys_id); //Set the value of vehicle tracker to the current records sys_id
        lvstgr.setValue('u_reference_2', current.vehicle); //Set the value of vehicle to the current records vehicle value
        lvstgr.setValue('u_choice_3', 'in service'); //Set the value of vehicle status to in service
        lvstgr.setValue('u_glide_date_4', currDate.getValue()); //Set the value of date sent for servicing to the current date
        lvstgr.setValue('u_choice_5', 'open'); //Set the value of ticket status to open
        lvstgr.insert(); //Insert the new record



    }
})(current, previous);
