sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here

	var vehTrackStat = current.vehicle_status; //get current vehicle status

	var lvcgr = new GlideRecord('x_823134_lva_loaner_vehicle_catalog'); //Glide record for loaner vehicle catalog

	lvcgr.addQuery('sys_id', current.vehicle); //Query loaner vehicle catalog table for the sys_id or the current vehicle field
	lvcgr.query(); //Execute query

	if (lvcgr.next()) {
		//Iterate through the vehicle tracker table and if the “if statements” are true for each vehicle tracker status 
		//change the status on the loaner vehicle catalog table as stated in the story

	if (vehTrackStat ==	'pending release' || vehTrackStat == 'out on field' || vehTrackStat == 'returned for inspection') {

		lvcgr.status = 'Unavailable'; //If the vehicle status on the tracker table is pending release, out on field, returned for inspection, or sent for servicing 
		//then set the status field on the loaner vehicle catalog table to unavailable
	}

    else if (vehTrackStat == 'returned to warehouse') {

		lvcgr.status = 'Available'; //If the vehicle status on the tracker table is returned to warehouse 
		//then set the status field on the loaner vehicle catalog table to available
}

	else if (vehTrackStat == 'decommissioned') {

		lvcgr.status = 'Decommissioned'; //If the vehicle status on the tracker table is decommissioned 
		//then set the status field on the loaner vehicle catalog table to decommissioned
	}

	lvcgr.update(); //Update the status field on the loaner vehicle

	}

})(current, previous);
